mapper.insert:
INSERT INTO ALLIM
SELECT SEQ_ALLIM_NO.NEXTVAL, A.* FROM (
	SELECT  #{allimContent} ALLIM_CONTENT, 
					'N' ALLIM_STATUS,
					MEMBER_NO 
					FROM WORKSPACE_JOIN 
					WHERE WORK_NO = #{workNo}
					AND MEMBER_NO != #{memberNo}
				
) A

mapper.select:
SELECT MEMBER_NO 
FROM WORKSPACE_JOIN
WHERE WORK_NO = #{workNo}
AND MEMBER_NO != #{memberNo}
