mapper.select:
SELECT MEMBER_RANK FROM INVITATION
WHERE MEMBER_NO = #{memberNo}
AND WORK_NO = #{workNo}

mapper.delete:
DELETE FROM INVITATION
WHERE MEMBER_NO = #{memberNo}
AND WORK_NO = #{workNo}

mapper.select:
SELECT CNT, (SELECT WORK_NM FROM WORKSPACE WHERE WORK_NO=#{workNo}) WORK_NM FROM (SELECT COUNT(*) CNT FROM WORKSPACE_JOIN
WHERE MEMBER_NO =#{memberNo}
AND WORK_NO = #{workNo})

mapper.update:
UPDATE WORKSPACE_JOIN SET MEMBER_RANK = #{memberRank}
WHERE WORK_NO = #{workNo}
AND MEMBER_NO = #{memberNo}

DELETE FROM WORKSPACE_JOIN
WHERE WORK_NO = #{workNo}
AND MEMBER_NO = #{memberNo}
