mapper.select:
SELECT * FROM MEMBER
WHERE SESSION_KEY = #{sessionId}
AND SESSION_LIMIT > SYSDATE

mapper.update:
UPDATE MEMBER SET
SESSION_KEY = #{memberId},
SESSION_LIMIT = #{limitDate}
WHERE MEMBER_ID = #{inputId}

UPDATE MEMBER SET
MEMBER_NICK = #{memberNick}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_IMG = #{fileName}
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT MEMBER_PW
FROM MEMBER		
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER SET
MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_PHONE = #{memberPhone}
AND MEMBER_NM = #{memberNm}
